// frontend/src/components/Login.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const navigate = useNavigate();

  const handleMockLogin = () => {
    // For development/demo purposes - bypass actual Spotify login
    localStorage.setItem("token", "mock_token");
    navigate("/dashboard");
  };

  const handleLogin = () => {
    // Real Spotify login - only use when backend is properly set up
    window.location.href = "http://localhost:8000/api/login";
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h1>Aura-fy Your Playlist</h1>
        <p>Discover the hilarious mood of your Spotify music</p>

        {/* Real Spotify login button (commented out for now) */}
        {/* <button className="spotify-login-btn" onClick={handleLogin}>
          Login with Spotify
        </button> */}

        {/* Demo button for testing without backend */}
        <button className="spotify-login-btn" onClick={handleMockLogin}>
          Try Demo (No Spotify Login Required)
        </button>

        <p style={{marginTop: '1rem', fontSize: '0.9rem', color: '#888'}}>
          Note: For full functionality, set up Spotify API credentials
        </p>
      </div>
    </div>
  );
};

export default Login;
